import React from "react";
import Axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
import { NavLink } from "react-router-dom";
import "./Home.css";

export default function CreateTweet() {
  const [loggedIn, setLoggedIn] = useState(false);
  useEffect(() => {
    Axios.get("/api/user/isLoggedIn")
      .then(() => {
        setLoggedIn(true);
      })
      .catch((err) => {
        setLoggedIn(false);
        console.log(err);
      });
  }, []);
  const [tweets, setTweets] = useState([]);
  const [tweetInput, setTweetInput] = useState({
    name: "",
    // health: 0,
    // user: "",
    // date: Date.now,
  });

  function getAllTweetData() {
    // let getPokemonData = null;
    Axios.get("/api/tweet").then(function (response) {
      setTweets(response.data);
    });
  }

  useEffect(function () {
    getAllTweetData();
  }, []);

  function onTweetInput(e) {
    const name = e.target.value;
    setTweetInput({
      ...tweetInput,
      name,
    });
  }

  function onSubmit() {
    Axios.post("/api/tweet", tweetInput)
      .then(function (response) {
        getAllTweetData();
      })
      .finally(function () {
        setTweetInput({
          name: "",
          // health: 0,
          // user: "",
          // date: Date.now,
        });
        location.reload();
      });
  }

  if (loggedIn) {
    return (
      <div className="page-body">
        <div>Add new Tweet:</div>
        <div>
          Tweet: <input value={tweetInput.name} onInput={onTweetInput} />
        </div>
        <div>
          <button onClick={onSubmit}>Submit</button>
        </div>
      </div>
    );
  } else {
    return (
      // Different styles for logged in.
      <div className="page-body">
        <div>Home Page</div>
        <ul>{tweet_components}</ul>
      </div>
    );
  }
}
