import React from "react";
import Axios from "axios";
import { useState } from "react";
import { useEffect } from "react";
import { NavLink } from "react-router-dom";
import "./Home.css";

export default function Home() {
  const [loggedIn, setLoggedIn] = useState(false);
  useEffect(() => {
    Axios.get("/api/user/isLoggedIn")
      .then(() => {
        setLoggedIn(true);
      })
      .catch((err) => {
        setLoggedIn(false);
        console.log(err);
      });
  }, []);
  const [tweets, setTweets] = useState([]);
  const [tweetInput, setTweetInput] = useState({
    name: "",
    // health: 0,
    user: "",
    date: Date.now,
  });

  function getAllTweetData() {
    // let getPokemonData = null;
    Axios.get("/api/tweet").then(function (response) {
      setTweets(response.data);
    });
  }

  useEffect(function () {
    getAllTweetData();
  }, []);

  function onTweetInput(e) {
    const name = e.target.value;
    setTweetInput({
      ...tweetInput,
      name,
    });
  }

  function onSubmit() {
    Axios.post("/api/tweet", tweetInput)
      .then(function (response) {
        getAllTweetData();
      })
      .finally(function () {
        setTweetInput({
          name: "",
          // health: 0,
          user: "",
          date: Date.now,
        });
      });
  }

  const tweet_components = [];
  for (let i = 0; i < tweets.length; i++) {
    const tweet = tweets[i];
    const tweet_component = (
      <li>
        <NavLink to={"/" + tweet._id}>{tweet.name}</NavLink>
      </li>
    );
    tweet_components.push(tweet_component);
  }
  if (loggedIn) {
    return (
      // Different styles for logged in.
      <div className="page-body">
        <div>Add new Tweet:</div>
        <div>
          Tweet: <input value={tweetInput.name} onInput={onTweetInput} />
        </div>
        <div>
          <button onClick={onSubmit}>Submit</button>
        </div>
        <ul>{tweet_components}</ul>
        <div>
          Add new Tweet:
          <div>
            Tweet: <input value={tweetInput.name} onInput={onTweetInput} />
          </div>
          {/* <div>
            Health:{" "}
            <input
              type="number"
              value={tweetInput.health}
              onInput={onHealthInput}
            />
          </div> */}
          {/* <div>
            User: <input value={tweetInput.user} onInput={onUserInput} />
          </div> */}
          <div>
            <button onClick={onSubmit}>Submit</button>
          </div>
        </div>
        <div>Here are all my Tweets: </div>
        <ul>{tweet_components}</ul>
        <div>{tweet_components}</div>
      </div>
    );
  } else {
    return (
      // Different styles for logged in.
      <div>
        <div>Here are all my Tweets: </div>
        <ul>{tweet_components}</ul>
      </div>
    );
  }
}
